mapper.delete:

delete from titles
where emp_no = #{empNo,jdbcType=INTEGER}
  and title = #{title,jdbcType=VARCHAR}
  and from_date = #{fromDate,jdbcType=DATE}

mapper.update:

update titles
set to_date = #{toDate,jdbcType=DATE}
where emp_no = #{empNo,jdbcType=INTEGER}
  and title = #{title,jdbcType=VARCHAR}
  and from_date = #{fromDate,jdbcType=DATE}

mapper.select:

select emp_no, title, from_date, to_date
from titles
where emp_no = #{empNo,jdbcType=INTEGER}
  and title = #{title,jdbcType=VARCHAR}
  and from_date = #{fromDate,jdbcType=DATE}
